mapper.select:
SELECT CASE
           WHEN NOT EXISTS (SELECT 1 FROM TB_PRODUCT WHERE V_PRODUCT_CD = #{vProductCd}) THEN 'INVALID'
           WHEN EXISTS (
               SELECT 1
               FROM TB_PRODUCT
               WHERE V_PRODUCT_CD = #{vProductCd} AND V_PRODUCT_MANUFACTOR = #{vIncomingProductSupplierNm}
           ) THEN 'VALID'
           ELSE 'UNLISTED'
           END AS result;

SELECT DISTINCT o.DT_ORDER_CREATED_DATE, o.PK_ORDER_SEQ, o.V_ORDER_TYPE, o.V_INCOMING_PRODUCT_SUPPLIER_NM, od.V_WAREHOUSE_CD, w.V_WAREHOUSE_NM, o.V_ORDER_STATUS, o.DT_ORDER_COMPLETION_DATE FROM TB_ORDER o JOIN TB_ORDER_DETAIL od ON o.PK_ORDER_SEQ = od.PK_ORDER_SEQ JOIN TB_WAREHOUSE w ON od.V_WAREHOUSE_CD=w.V_WAREHOUSE_CD WHERE o.DT_ORDER_CREATED_DATE BETWEEN #{startDate} AND #{endDate}
AND o.V_INCOMING_PRODUCT_SUPPLIER_NM LIKE #{vIncomingProductSupplierNm}
AND od.V_WAREHOUSE_CD LIKE #{vWarehouseCd}
AND o.V_ORDER_STATUS LIKE #{vOrderStatus}
ORDER BY o.PK_ORDER_SEQ
